mapper.select:
SELECT A.diary_id, A.title, C.file_path, A.body, A.study_content, A.created_at
FROM diary_posts A
LEFT OUTER JOIN diary_images C ON A.diary_id = C.diary_id
WHERE A.user_id = #{userId}
AND (
C.created_at = (
SELECT MAX(created_at)
FROM diary_images B
WHERE A.diary_id = B.diary_id
)
OR C.created_at IS NULL
);

SELECT diary_id, A.user_id, title, body, A.created_at, study_content, nickname
FROM diary_posts A inner join users B ON A.user_id=B.user_id
WHERE diary_id = #{diaryId}

SELECT file_path
FROM diary_images
WHERE diary_id=#{diaryId}

mapper.update:
UPDATE diary_posts
SET title=#{title}, study_content=#{studyContent}, body=#{body}
WHERE user_id=#{userId} AND diary_id=#{diaryId}
